    "detail_find_rule": "js:\nvar res = {};\nvar d = [];\nMY_URL=MY_URL.replace('hiker://empty##','');\nvar html = fetch(MY_URL);\nconst Color = \"#11999e\";\n\nvar title = pdfh(html, 'h1&&Text');\nvar des_desc = pdfh(html, '.fa:contains(简介)&&Text').replace('展开全部', '').replace('简介：', '').replace('   ', '').replace('bbb', '').replace('ccc', '').replace('eee', '');\n//二级新样式by顺承天意（已封装断插，变量名lazy）\nconst hd = require(\"http://hiker.nokia.press/hikerule/rulelist.json?id=2968&v=1\");\nhd(d, {\n    //显示的白色大字\n    大字: title,\n    //片名搜索用\n    片名: title,\n    //图片一定不要加Referer\n    图片: pd(html, '.lazyload&&data-original'),\n    //描述里用<br>换行\n    描述: pdfh(html, 'body&&.col33,0&&Text').substring(0, 18) + '<br>' + pdfh(html, 'body&&.col33,1&&Text').substring(0, 18) + '<br>' + pdfh(html, 'body&&.col33,4&&Text').substring(0, 18) + '<br>简介: ' + des_desc,\n});\n\nvar tabs = pdfa(html, 'body&&.playlist-tab&&a'); //线路\nvar conts = pdfa(html, 'body&&.con_c2_list'); //选集\n\neval(JSON.parse(fetch('hiker://page/lazy', {})).rule);\n\n\nfor (var i = 0; i < conts.length; i++) {\n    var icon_s = 'https://ghproxy.com/https://raw.githubusercontent.com/ls125781003/tubiao/main/messy/';\n    if (getVar('shsort', '1') == '1') {\n        var list = parseDomForArray(conts[i], 'body&&a');\n        var avatar = icon_s + '129.svg'\n    } else {\n        var list = parseDomForArray(conts[i], 'ul&&li').reverse();\n        var avatar = icon_s + '132.svg'\n    }\n    var line = pdfh(tabs[i], 'a&&Text').replace('-官方', '').replace('备用资源', '');\n\n    if (list != null) {\n        d.push({\n            title: '<span style=\"color:' + Color + '\">' + line + '</span>' + '\\t\\t' + '<small>' + (i + 1) + '／' + conts.length + '          ',\n            url: \"hiker://empty@lazyRule=.js:putVar('shsort', getVar('shsort','1')=='1'?'0':'1');refreshPage();'hiker://empty'\",\n            col_type: 'avatar',\n            img: avatar\n        });\n\n        for (var j = 0; j < list.length; j++) {\n            var jm = pdfh(list[j], 'a&&Text').replace(/第|集|话|期/g, '').replace(/预告/g, '📢');\n            var url = pd(list[j], 'a&&href');\n            if (list.length <= 2) {\n                var col = 'text_2'\n            } else {\n                var col = jm.length > 4 ? 'text_2' : 'text_4'\n            }\n            d.push({\n                title: jm,\n                url: url + lazy,\n                col_type: col,\n                extra: {\n                    id: url,\n                    blockRules: [\n                        \".jpg\",\n                        \".jpeg\",\n                        \".png\",\n                        \".mp4\",\n                        \".m3u8\",\n                        \".gif\"\n                    ]\n                }\n            });\n        }\n    }\n}\nres.data = d;\nsetHomeResult(res);",
